mapper.sql:
select a.PARENT as CITY, a.ID as AREA, count(d.ID) as SERVICE_TIMES, count(distinct c.ID, if(d.ID is not null, 1, null)) as SERVICE_PERSON, count(distinct c.ID, if(d.STRANGER, 1, null)) as STRANGER, count(distinct c.ID, if(c.SEX='男' and d.ID is not null, 1, null)) as MALES, count(distinct c.ID, if(date_add(c.BIRTHDAY, interval + 35 year) >= d.SERVICE_DATE, 1, null)) as AGE_LE35, sum(IFNULL(d.TRANS_MMT,0)) + sum(IFNULL(d.TRANS_VCT, 0)) as TRANS_TIMES, count(distinct c.ID, if(d.TRANS_MMT or d.TRANS_VCT, 1, null)) as TRANS_PERSON, count(distinct c.ID, if((c.POSITIVE and d.ID is not null) or exists( select 1 from t4_work_record s where s.OBJECT_ID=c.ID
and s.SERVICE_DATE < #{queryForm.queryFrom, jdbcType=DATE}
and s.TRANS_VCT and s.HIV_RESULT <> '阴性') , 1, null)) as POSITIVE, count(distinct c.ID, if(c.POSITIVE and d.ID is not null, 1, null)) as ORIGINAL_POSITIVE, count(distinct c.ID, if(d.HIV_RESULT <> '阴性', 1, null)) as HIV_NEW_CASES, count(distinct c.ID, if(d.TRANS_VCT, 1, null)) as HIV_EXAMINED, count(if(d.TRANS_MMT, 1, null)) as TRANS_MMT, count(distinct c.ID, if(b.HAS_MMT and d.ID is not null, 1, null)) as TRANS_MMT_BASE, count(distinct c.ID, if(d.TRANS_ANTI_THERAPY, 1, null)) as TRANS_ANTI_THERAPY, sum(IFNULL(d.NEEDLE_PROVIDED, 0)) as NEEDLE_PROVIDED, sum(IFNULL(d.NEEDLE_RECYCLED, 0)) as NEEDLE_RECYCLED, sum(IFNULL(d.CONDOMS_PROVIDED,0)) as CONDOMS_PROVIDED, sum(IFNULL(d.IEC_DOC_PROVIDED,0)) as IEC_DOC_PROVIDED from t4_c_area a left join t4_org b on b.AREA = a.ID and b.VALID and b.TYPE='交换点' left join t4_objects c on c.SITE = b.ID left join t4_work_record d on d.OBJECT_ID = c.ID
and d.SERVICE_DATE >= #{queryForm.queryFrom, jdbcType=DATE}
and d.SERVICE_DATE <= #{queryForm.queryTo, jdbcType=DATE}
where a.TYPE='COUNTY'
and a.ID = #{queryForm.queryArea, jdbcType=INTEGER}
and a.PARENT = #{queryForm.queryCity, jdbcType=INTEGER}
group by a.PARENT, a.ID with rollup

select a.PARENT as CITY, a.ID as AREA, count(b.ID) as DUMMY from t4_c_area a left join t4_org b on b.AREA = a.ID where a.TYPE='COUNTY'
and a.ID = #{queryForm.queryArea, jdbcType=INTEGER}
and a.PARENT = #{queryForm.queryCity, jdbcType=INTEGER}
group by a.PARENT, a.ID with rollup
